$(document).ready(function(){$(".slider-contents").slick({slidesToShow:1,slidesToScroll:1,autoplay:!0,autoplaySpeed:5e3,arrows:!0,accessibility:!0}),[].slice.call(document.querySelectorAll('a[href="#"')).forEach(function(o){o.addEventListener("click",function(o){o.preventDefault()})})}),$(document).scroll(function(){$("body").scrollTop()>0?$(".header").css({"border-bottom":"1px solid #ecf0f1"}):$("body").scrollTop()<=0&&$(".header").css({"border-bottom":"none"})});